INSERT INTO Answer (id, questionId, progressReportId, answer) VALUES ('u_6uxc9tt92me6gnb1s', 'q4', 'u_t2jbs9jrime6gnamz', 'El tema de pensamiento crítico se conecta con otras materias como Biología, se analiza información científica y se buscan evidencias. También con Matemática, donde es necesario interpretar datos y planificar procedimientos.');
